import React, { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import Navbar from "../components/Navbar";
import deleteProjectAllocation from "../hooks/deleteProjectAllocation";
import postProjectAllocationByUsername from "../hooks/postProjectAllocations";
import useProject from "../hooks/useProject";
import useProjectDepartment from "../hooks/useProjectDepartment";

function ProjectDepartment() {
  let { projectId, departmentId } = useParams();

  let rawDepartmentStats = useProjectDepartment(projectId, departmentId);
  let rawProjectInfo = useProject(projectId);
  let urlBuilder = projectId + "/" + departmentId;

  let departmentStats = null;

  if (rawProjectInfo && rawDepartmentStats && departmentStats == null) {
    departmentStats = {
      project: rawProjectInfo.name,
      color: rawProjectInfo.color,
      name: rawDepartmentStats[0].User.Department.name,
      teamLead: rawDepartmentStats[0].User.name,
    };
  } else {
    departmentStats = {
      project: "ING",
      color: "#ffa500",
      name: "IT",
      teamLead: "Baltac Mihai-Cristian",
    };
  }

  let members = null;

  if (rawDepartmentStats && members == null) {
    members = rawDepartmentStats.map((member) => {
      return {
        id: member.User.id,
        photo: member.User.photo,
        name: member.User.name,
      };
    });
  } else {
    members = [
      {
        photo:
          "https://upload.wikimedia.org/wikipedia/commons/thumb/b/bc/Unknown_person.jpg/925px-Unknown_person.jpg",
        name: "Baltac Mihai-Cristian",
      },
      {
        photo:
          "https://upload.wikimedia.org/wikipedia/commons/thumb/b/bc/Unknown_person.jpg/925px-Unknown_person.jpg",
        name: "Baltac Mihai-Cristian",
      },
    ];
  }

  useEffect(() => {
    if (departmentStats) {
      let projectColor = document.getElementById("js-title");
      projectColor.style.backgroundColor = departmentStats.color;
    }
  });

  return (
    <div className="page project_page department_page">
      <Navbar current="projects" />
      {returnProjectPage(departmentStats, members, urlBuilder, projectId)}
    </div>
  );
}

function returnProjectPage(departmentStats, members, urlBuilder, projectId) {
  return (
    <div className="project_page_content">
      {returnDepartmentStats(departmentStats)}

      <div className="row">
        {returnEvents(urlBuilder)}
        {returnMembers(members)}
        {returnMembersModal(members, projectId)}
      </div>
    </div>
  );
}

function returnDepartmentStats(departmentStats) {
  return (
    <div className="card">
      <div className="row">
        <div className="card_item ">
          <div className="card_item">
            <p className="card_title ">
              <p id="js-title">{departmentStats.project}</p>
            </p>
            <p className="task_title">
              <strong>{departmentStats.name}</strong> Department
            </p>
          </div>
        </div>
        <p className="card_item">
          Team lead: <strong>{departmentStats.teamLead}</strong>
        </p>
      </div>
    </div>
  );
}

function returnEvents(urlBuilder) {
  let meetingLink = "/meetings/" + urlBuilder;
  let taskLink = "/tasks/" + urlBuilder;
  return (
    <div className="section">
      <p className="title">Events</p>
      <div className="row">
        <Link to={meetingLink}>
          <div className="department">
            <img
              src="data:image/svg+xml;base64,"
              alt="Meetings"
              className="department_logo"
            />
            <p className="department_name">Meetings</p>
          </div>
        </Link>
        <Link to={taskLink}>
          <div className="department">
            <img
              src="data:image/svg+xml;base64,"
              alt="tasks"
              className="department_logo"
            />
            <p className="department_name">Tasks</p>
          </div>
        </Link>
      </div>
    </div>
  );
}

function returnMembers(members) {
  return (
    <div className="section">
      <div className="row row-around">
        <p className="title" id="title">
          Members
        </p>
        <span
          className="icon-pencil icon"
          onClick={(e) => {
            displayStatusModal("seeMembers", "flex");
          }}
        ></span>
      </div>
      <div className="column">
        {members?.map((e) => {
          return returnMember(e);
        })}
      </div>
    </div>
  );
}
function displayStatusModal(location, type) {
  let statusModal = document.getElementById(location);

  statusModal.style.display = type;
}
function returnMembersModal(members, projectId) {
  return (
    <div className="modal" id="seeMembers">
      <div className="modal_content">
        <span
          className="icon-cross close"
          onClick={(e) => {
            displayStatusModal("seeMembers", "none");
          }}
        ></span>
        <div className="modal_label modal_label-icon">
          <input
            type="text"
            name="add_memeber"
            id="add_memeber"
            placeholder="Write username"
          />
          <span
            className="icon-plus icon"
            onClick={(e) => {
              let body = {
                username: document.getElementById("add_memeber").value,
                projectId: projectId,
                type: "EMPLOYEE",
              };
              postProjectAllocationByUsername(body);
            }}
          ></span>
        </div>
        <div className="members">
          {members.map((member) => {
            console.log(members);
            return (
              <div className="member">
                <div className="member_infos">
                  <img
                    src={member.photo}
                    alt={member.name + " photo"}
                    className="member_infos_photo"
                  />
                  <p className="member_infos_name">{member.name}</p>
                </div>

                <span
                  className="actions icon-cross"
                  onClick={(e) => {
                    deleteProjectAllocation(projectId, member.id);
                  }}
                ></span>
              </div>
            );
          })}
        </div>
        <div className="modal_actions  modal_actions-one">
          <p
            className="cancel"
            onClick={(e) => {
              displayStatusModal("seeMembers", "none");
            }}
          >
            Cancel
          </p>
        </div>
      </div>
    </div>
  );
}

function returnMember(member) {
  return (
    <div className="member_card">
      <img
        className="member_card_photo"
        src={
          member.photo
            ? member.photo
            : "https://upload.wikimedia.org/wikipedia/commons/thumb/b/bc/Unknown_person.jpg/925px-Unknown_person.jpg"
        }
      ></img>

      <p className="member_card_name">{member.name}</p>
    </div>
  );
}

export default ProjectDepartment;
